import React, {useState} from "react";
import { getAuth, signInWithPopup, GoogleAuthProvider, signOut } from "firebase/auth";
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";

const firebaseConfig = {
    apiKey: "AIzaSyA3en2vVlf41XtHkjnbQFJhLN9CCbOeF18",
    authDomain: "single-sign-on-183d9.firebaseapp.com",
    projectId: "single-sign-on-183d9",
    storageBucket: "single-sign-on-183d9.firebasestorage.app",
    messagingSenderId: "976885085363",
    appId: "1:976885085363:web:b18faf4bd2369147956cc6",
    measurementId: "G-JRL0YJM0PF"
};

const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);
const auth = getAuth(app);
const provider = new GoogleAuthProvider();

export default function SSOLogin() {
    const[user, setUser] = useState(null);
    const handleLogin = async() => {
        try{
            const result = await signInWithPopup(auth, provider);
            setUser(result.user);
        }
        catch(error){
            console.log("Login failed" , error);
        }
    }
    const handleLogout = async() => {
        try{
            await signOut(auth);
            setUser(null);
        }
        catch(error){
            console.log("Logout failed", error);
        }
    }

    return(
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <div className="bg-white shadow-md rounded-2xl p-6 max-w-sm text-center">
        {user ? (
          <div>
            <h2 className="text-xl font-semibold mb-4">Welcome, {user.displayName}</h2>
            <img className="rounded-full w-24 h-24 mx-auto mb-4" src={user.photoURL} alt="User profile" />
            <button 
              className="bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded-xl"
              onClick={handleLogout}
            >
              Logout
            </button>
          </div>
        ) : (
          <button 
            className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded-xl"
            onClick={handleLogin}
          >
            Sign in with Google
          </button>
        )}
      </div>
    </div>
    );
}
